mapper.insert:
INSERT INTO player ( id
                 , userid
                 , currentlevel
                 , language
                 , exppoint
                 , score
                 , heart
) VALUES (
             #{id}
         , #{userId}
         , #{currentLevel}
         , #{language}
         , #{expPoint}
         , #{score}
         , #{heart}
         );

mapper.update:
UPDATE player
SET CURRENTLEVEL = #{currentLevel},
    LANGUAGE = #{language},
    EXPPOINT = #{expPoint},
    LASTUPDATEDATE = #{lastupdatedate}
WHERE ID=#{id}
